package icehs.science.chapter04;

public class AndOrOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=15, b=16;
		System.out.println(a<=b);
		System.out.println(a==b);
		System.out.println((a<=b)&(a==b));
		System.out.println((a<=b)|(a==b));
		System.out.println((a<=b)||(++a==b));
		System.out.println(a);
	}

}
