package icehs.science.chapter04;

public class CalculatorTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 20, num2 = 7;
		int sum = num1+num2;
		int cha = num1-num2;
		int gop = num1*num2;
		int nanut = num1/num2;
		int namogi = num1%num2;
		System.out.println("오 써쎀 : "+sum);
		System.out.println("짋 써쎀 : "+cha);
		System.out.println("썼 써쎀 : "+gop);
		System.out.println("씱얋 써쎀 : "+nanut);		
		System.out.println("씱쟵쵔 써쎀 : "+namogi);		
	}

}
