package icehs.science.chapter04;

public class InDeOperatorEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1=10, n2=3;
		System.out.println(++n1);
		System.out.println(n2++);
		int result = --n1 + n2--;
		System.out.println(result);
		
	}

}
