package icehs.science.chapter05;

public class ConditionIfEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 20;
		if(age<19) {
			System.out.println("19세 이상만 입장 가능합니다.");
		}
		if(age>=19) {
			System.out.println("즐거운 시간 보내시길 바랍니다.");
		}
	}

}
